import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const TESTS = [
  { id: 'face', label: 'Face (1/4)', path: '/test-facial' },
  { id: 'arm-right', label: 'Braço Direito (2/4)', path: '/test-arm-right' },
  { id: 'arm-left', label: 'Braço Esquerdo (3/4)', path: '/test-arm-left' },
  { id: 'speech', label: 'Fala (4/4)', path: '/test-speech' },
];

export default function TestQuickMenu({ currentTest }) {
  const navigate = useNavigate();
  const [open, setOpen] = useState(false);

  const options = TESTS.filter((test) => test.id !== currentTest);

  const handleNavigate = (path) => {
    setOpen(false);
    navigate(path);
  };

  return (
    <div className="test-quick-menu">
      <button
        type="button"
        className="button outline small"
        onClick={() => setOpen((prev) => !prev)}
      >
        ➜ Ir para outro teste
      </button>
      {open && (
        <div className="test-quick-menu__dropdown">
          {options.map((option) => (
            <button
              key={option.id}
              type="button"
              onClick={() => handleNavigate(option.path)}
            >
              {option.label}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}
